int sensor = A0;     // Connect gas sensor output to analog pin A0
int val = 0;         // Variable to store the gas sensor reading
int limit = 47;      // Threshold value after mapping; if gas exceeds this value, the buzzer will sound

void setup() {
  Serial.begin(9600);  // Initialize the Serial Monitor at 9600 baud to print messages like "Gas detected"
}

void loop() {
  val = analogRead(sensor);  // Get a value between 0‚Äì1023 depending on the gas concentration
  val = map(val, 306, 750, 0, 100);  // Convert that value into a 0‚Äì100 range (calibrated for your specific sensor)
                                   // Example: 306 ‚Üí 0% gas, 750 ‚Üí 100% gas

  if(val > limit) {
    tone(8, 650);                  // Play a sound at 650Hz on pin 8 (buzzer)
    Serial.println("Gas detected"); // Print a warning
    Serial.print("Gas Level: "); 
    Serial.println(val); 
  }

  delay(300);     // Wait for 300 milliseconds
  noTone(8);      // Turn off the buzzer
}

