#include <Keypad.h>
#include <Servo.h>
Servo servo;
char* password = "1234";
char entered[5];
int pos = 0;
const byte rows = 4;
const byte cols = 4;
char keys[rows][cols] = {
  {'1','2','3','A'},
  {'4','5','6','B'},
  {'7','8','9','C'},
  {'*','0','#','D'}
};
byte rowPins[rows] = {9, 8, 7, 6};
byte colPins[cols] = {5, 4, 3, 2};
Keypad keypad = Keypad(makeKeymap(keys), rowPins, colPins, rows, cols);
void setup(){
  servo.attach(10);
  servo.write(0);
  Serial.begin(9600);
}
void loop(){
  char key = keypad.getKey();
  if(key){
    entered[pos] = key;
    pos++;
    if(pos == 4){
      entered[4] = '\0';
      if(strcmp(entered, password) == 0){
        servo.write(90);
        delay(2000);
        servo.write(0);
      }
      pos = 0;
    }
  }
}

#include <Servo.h>
Servo lid;
int trig = 9, echo = 10;
void setup(){
  lid.attach(6);
  pinMode(trig, OUTPUT);
  pinMode(echo, INPUT);
  Serial.begin(9600);
}
void loop(){
  digitalWrite(trig, LOW);
  delayMicroseconds(2);
  digitalWrite(trig, HIGH);
  delayMicroseconds(10);
  digitalWrite(trig, LOW);
  long t = pulseIn(echo, HIGH);
  long cm = t / 29 / 2;
  Serial.println(cm);
  if(cm < 15){
    lid.write(90);
    delay(3000);
    lid.write(0);
  }
  delay(500);
}

